import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router";
function usePrevious(value) {
    const ref = useRef();
    useEffect(() => {
      ref.current = value;
    });
    return ref.current;
  }
function Enemy(props){
    const [skills,setSkills] = useState([]);
    const [isExpand, setExpand] = useState(false);
    const navigate = useNavigate();
    useEffect(()=>{
        async function getSkills(){
          const response = await fetch(`http://localhost:3500/api/enemies/${props.id}`);
          if (!response.ok) {
            const message = `An error occurred: ${response.statusText}`;
            window.alert(message);
            return;
          }
          const data = await response.json();
          setSkills(data);
        }
        getSkills();
        return;
    },[skills.length])
    const expandTemplate = (
        <div className="displayBlock">
          <div>
              <label className="enemy-label" htmlFor={props.id}>name: {props.name}</label>
              <button onClick={()=>AddSkill(props.id)}>Add Skill</button>
              <button onClick={()=>setExpand(false)}>ChangeDisplay</button>
        </div>
          <div>
              <p>description: {props.description}</p>
          </div>
          <div>
              
          </div>
        </div>
    );
    function AddSkill(id){
        navigate('/AddSkill',{state:{id}});
    }
    const listTemplate = (
        <div className="displayBlock">
            <div>
                <label className="enemy-label" htmlFor={props.id}>name: {props.name}</label>
                <button onClick={()=>AddSkill(props.id)}>Add Skill</button>
                <button onClick={()=>setExpand(true)}>ChangeDisplay</button>
            </div>
            <div>
                <p>description: {props.description}</p>
            </div>
        </div>
    );
    return <li className="todo">{isExpand ? expandTemplate : listTemplate}</li>;
}

export default Enemy;
