import React, { useEffect, useRef, useState } from "react";
function usePrevious(value) {
    const ref = useRef();
    useEffect(() => {
      ref.current = value;
    });
    return ref.current;
  }
function Enemy(props){
    const [isExpand, setExpand] = useState(false);
    const [newName, setNewName] = useState("");
    const editFieldRef = useRef(null);
    const editButtonRef = useRef(null);
    const wasExpand = usePrevious(isExpand);
    const expandTemplate = (
        <div>
            <label className="enemy-label" htmlFor={props.id}>{props.name}</label>
        </div>
    );
    const listTemplate = (
        <div className="displayBlock">
            <div>
                <label className="enemy-label" htmlFor={props.id}>name: {props.name}</label>
                <button onClick={AddSkill}>Add Skill</button>
                <button onClick={()=>setExpand(true)}>ChangeDisplay</button>
            </div>
            <div>
                <p>description: {props.description}</p>
            </div>
        </div>
    );
    useEffect(() => {
        if (!wasExpand && isExpand) {
          editFieldRef.current.focus();
        }
        if (wasExpand && !isExpand) {
          editButtonRef.current.focus();
        }
      }, [wasExpand, isExpand]);
      return <li className="todo">{isExpand ? expandTemplate : listTemplate}</li>;
}

export default Enemy;
